#include<iostream> #include<cstring> using namespace std;int a[100002],b[100002],n;  int main()  {      while(cin >> n)      {      	memset(a,0,sizeof(a));    	memset(b,0,sizeof(b));        int ans=0;          for(int i=n+1;i<=2*n;i++)          {              if(!((i*n)%(i-n)))            {                  ans++;                  a[ans]=(i*n)/(i-n);                  b[ans]=i;              }          }          cout << ans << endl;          for(int i=1;i<=ans;i++) printf("1/%d = 1/%d + 1/%d\n",n,a[i],b[i]);      }  }   